sysauto_script.script:
//Find the current time
var rightNow = new GlideDateTime();
//Query the table for upcoming due dates
var upcomingNITasks = new GlideRecord('x_58872_needit_needit_task');
upcomingNITasks.addQuery('due_date', '<=', gs.hoursAgo(-24));
upcomingNITasks.addQuery('due_date', '>', rightNow);
upcomingNITasks.addQuery('state', '!=', 3);
upcomingNITasks.query();
while(upcomingNITasks.next()){
	gs.info("Task is due in under 24 hours: " + upcomingNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask', upcomingNITasks, upcomingNITasks.number, gs.getUserName());
}
